newline|'\n'
nl|'\n'
name|'column'
op|'='
string|"r'''\n  \\s*           # any number of whitespace\n  \\*{1}(?![*/]) # followed by precisely one asterisk not followed by `/'\n  (.*)          # then anything (group1)\n'''"
